function Coffee() {
	// 湯気の3本の線を定義
	const steamPaths = [
		// 1本目：左側に少し大きく揺れる
		{
			index: 0,
			id: "steam-left",
			d: "M 498 351 L 504.5 351 L 514.5 356 L 535 374.5 L 543 390.5 L 545 400.5 L 545 418.5 L 541 433.5 L 525 463.5 L 524 474.5 L 528.5 482 L 537.5 489 L 542.5 491 L 544.5 491 L 547 492.5 L 545.5 495 Q 538.5 496 535.5 493 L 522 481.5 L 520 477.5 L 520 464.5 L 538 428.5 L 540 421.5 L 541 407.5 L 540 406.5 L 540 398.5 L 538 389.5 L 529 374 L 509.5 358 L 498 354 L 498 351 Z ",
		},
		{
			index: 1,
			id: "steam-center",
			d: "M 595.5 391 L 600 393.5 L 609 405.5 L 615 421.5 L 616 425.5 L 616 446.5 L 614 455.5 L 605 471.5 L 604 486.5 Q 605.4 492.6 610.5 495 L 621 501.5 L 619.5 505 L 617.5 505 L 602 492.5 Q 598.4 487.1 600 476.5 L 605 460.5 L 610 453.5 L 612 442.5 L 612 428.5 L 607 412.5 L 597 397 Q 594.3 398.1 595 395.5 L 594 392.5 L 595.5 391 Z ",
		},
		{
			index: 2,
			id: "steam-right",
			d: "M 455 409 L 464.5 409 L 466.5 410 L 477 420.5 L 482 429.5 L 483 444.5 L 479 457.5 L 469 473.5 L 466 485.5 L 466 497.5 L 473 511 L 483.5 517 L 486 517 Q 487 520.8 484.5 522 Q 473 519 467 510.5 L 462 499.5 L 462 483.5 L 465 471.5 L 477 449.5 L 478 433.5 Q 474 420.5 463.5 414 Q 456.8 415.8 455 412.5 L 455 409 Z ",
		},
	];

	return (
		<div className={styles.coffee}>
			<svg
				className={styles.steam}
				viewBox="450 350 180 180" // 湯気がある範囲にズーム
				xmlns="http://www.w3.org/2000/svg"
			>
				<title>Decorative Steam Animation</title>
				{steamPaths.map((stream) => (
					<motion.path
						key={stream.id}
						d={stream.d}
						fill="var(--dark)" // 塗りつぶしを適用
						initial={{ opacity: 0, y: 20, x: 0 }}
						animate={{
							opacity: [0, 0.7, 0], // 現れて消える
							y: [20, 0, -30], // 下から上へ昇る
							x: [0, stream.index % 2 === 0 ? 5 : -5, 0], // 左右の揺らぎ
						}}
						transition={{
							duration: 2,
							repeat: Infinity,
							delay: stream.index * 0.7, // 各湯気の時間差を広めに
							ease: "easeInOut", // ぬるっとした動きに
						}}
					/>
				))}
			</svg>
			<CoffeeCup />
		</div>
	);
}

export default Coffee;
